#include <iostream>
#include <vector>
#include <queue>
#include <unordered_set>

// Функция BFS для обхода графа
void BFS(const std::vector<std::vector<int>>& graph, int start, std::vector<bool>& visited) {
    std::queue<int> q;
    q.push(start);
    visited[start] = true;

    while (!q.empty()) {
        int current = q.front();
        q.pop();

        for (int neighbor : graph[current]) {
            if (!visited[neighbor]) {
                visited[neighbor] = true;
                q.push(neighbor);
            }
        }
    }
}

// Функция для проверки связности графа
bool isGraphConnected(const std::vector<std::vector<int>>& graph, int numNodes) {
    std::vector<bool> visited(numNodes, false);
    BFS(graph, 0, visited); // Запускаем BFS из узла 0

    // Проверяем, все ли узлы посещены
    for (bool v : visited) {
        if (!v) {
            return false; // Граф не связный
        }
    }
    return true; // Граф связный
}

